func threeSum(nums []int) (combination [][]int) {
	mapExist := make(map[string]bool)
	if len(nums) <= 2 {
		return
	}
	sort.Ints(nums)
	for i := 0; i < len(nums)-2; i++ {
		if nums[i] > 0 {
			break
		}
		if i > 0 && nums[i] == nums[i-1] {
			continue
		}
		for j := i + 1; j < len(nums)-1; j++ {
			for k := j + 1; k < len(nums); k++ {
				if nums[i]+nums[j]+nums[k] == 0 {
					combinationHit := []int{nums[i], nums[j], nums[k]}
					strCombinationHit := transformArray(combinationHit)
					if _, ok := mapExist[strCombinationHit]; !ok {
						mapExist[strCombinationHit] = true
						combination = append(combination, combinationHit)
					}
				}
			}
		}
	}
	return
}

func transformArray(nums []int) (transformedNumber string) {
	for _, v := range nums {
		strNumber := strconv.Itoa(v)
		transformedNumber += strNumber
	}
	return
}
